import React, { Component } from 'react'

const hover = {
  backgroundColor: '#f1f1f1',
  cursor: 'pointer'
}

const active = {
  backgroundColor: 'dodgerblue',
  color: '#ffffff'
}

const style = {
  padding: '7px',
  fontSize: '16px',
  fontWeight: 100,
  hover: hover,
  active: active
}

const LiCons = (props) => {
  const _style = { ...style, ...props.style }

  return (
    <li
      style={
        props.actived
          ? { ..._style, ...style.active, ..._style.active }
          : props.hovered
          ? { ..._style, ...style.hover, ..._style._hover }
          : _style
      }
      onMouseEnter={props.onMouseEnter}
      onMouseLeave={props.onMouseLeave}
      onMouseDown={props.onClick}
    >
      {props.children}
    </li>
  )
}

class Suggestion extends Component {
  constructor(props) {
    super(props)
    this.state = {
      hovered: false,
      actived: false
    }
  }

  handleOnMouseEnter = (e) => {
    this.setState({ hovered: true })
  }

  handleOnMouseLeave = (e) => {
    this.setState({ hovered: false })
  }

  render() {
    const { actived, children, onClick, style } = this.props
    const { hovered } = this.state

    return (
      <LiCons
        style={style}
        hovered={!!hovered}
        actived={!!actived}
        onMouseEnter={this.handleOnMouseEnter}
        onMouseLeave={this.handleOnMouseLeave}
        onClick={onClick}
      >
        {children}
      </LiCons>
    )
  }
}

Suggestion.propTypes = {
  // key: PropTypes.number.isRequired,
  // text: PropTypes.string.isRequired
}

export default Suggestion
import React, { Component } from 'react'
import Suggestion from './Suggestion'

const style = {
  position: 'absolute',
  listStyleType: 'none',
  margin: 0,
  padding: 0,
  border: '1px solid #f1f1f1',
  borderTop: 0,
  borderRadius: '3px',
  borderTopRightRadius: 0,
  borderTopLeftRadius: 0,
  maxHeight: 'calc(~"100vh - 100px")',
  maxWidth: '300px',
  overflowY: 'auto',
  backgroundColor: 'rgba(255,255,255,1)',
  textAlign: 'left',
  // bug when position absolute and under center tag, center is ignored
  marginLeft: 'auto',
  marginRight: 'auto',
  left: 0,
  right: 0
  // end bug position
}

const UlCons = (props) => {
  return <ul style={{ ...style, ...props.style }}>{props.children}</ul>
}

export default class Suggestions extends Component {
  castToSuggestionItems(items, activedIdx, onClick) {
    return items.map((item, idx) => (
      <Suggestion
        key={idx}
        actived={activedIdx === idx}
        onClick={() => onClick(idx)}
      >
        {item.getSuggestion()}
      </Suggestion>
    ))
  }

  render() {
    const { children, onClick, activedIndex, style } = this.props

    return (
      <UlCons style={style}>
        {this.castToSuggestionItems(children, activedIndex, onClick)}
      </UlCons>
    )
  }
}
import React, { Component } from 'react'
import Suggestions from './Suggestions'

const InputCons = (props) => {
  return (
    <input
      {...props}
      ref={(input) => props.inputRef(input)}
      type='text'
      autoComplete='off'
    />
  )
}

class InputAutoSuggest extends Component {
  constructor(props) {
    super(props)
    this.state = {
      activedIndex: -1,
      filteredData: [],
      data: this.getData(props.data)
    }

    String.prototype.bold = function (bold) {
      const indexOfSearch = this.toLowerCase().indexOf(bold.toLowerCase())

      if (indexOfSearch >= 0) {
        return (
          <div>
            {this.substr(0, indexOfSearch)}
            <b>{this.substr(indexOfSearch, bold.length)}</b>
            {this.substr(indexOfSearch + bold.length)}
          </div>
        )
      } else {
        return this
      }
    }

    Array.prototype.where = function (condition) {
      var ret = []

      for (let i = 0; i < this.length; i++) {
        if (condition(this[i].originalData)) {
          ret.push(this[i])
        }
      }

      return ret
    }
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.data) {
      const data = this.getData(nextProps.data)
      if (nextProps.data !== data) {
        this.setState({ data: data })
      }
    }
  }

  getData(propData) {
    return propData.map((item) => {
      return {
        getSuggestion: () => this.display(item, this.props.value),
        originalData: item
      }
    })
  }

  handleSelect = (idx) => {
    const { name, onChange } = this.props
    const e = {
      target: {
        name: name,
        value: this.state.filteredData[idx].originalData
      }
    }

    this.setState({
      filteredData: []
    })

    onChange(e)
  }

  display(item, search) {
    const hasAnySearch = search
    const hasSpecialDisplayTreatment = this.props.display

    if (hasSpecialDisplayTreatment) {
      const displayItem = this.props.display(item)
      const isStringType = typeof displayItem === 'string'

      if (hasAnySearch && isStringType) {
        return displayItem.bold(search)
      } else {
        return displayItem
      }
    } else {
      if (hasAnySearch) {
        return item.bold(search)
      } else {
        return item
      }
    }
  }

  handleOnKeyDown = (e) => {
    const { activedIndex, filteredData } = this.state

    switch (e.key) {
      case 'ArrowUp':
        e.preventDefault()
        if (activedIndex > 0) this.setState({ activedIndex: activedIndex - 1 })
        break
      case 'ArrowDown':
        if (activedIndex < filteredData.length - 1)
          this.setState({ activedIndex: activedIndex + 1 })
        break
      case 'Enter':
        if (activedIndex !== -1) this.handleSelect(activedIndex)
        break
      case 'Escape':
        this.setState({ filteredData: [] })
        break
      default:
        if (activedIndex !== -1) this.setState({ activedIndex: -1 })
    }
  }

  handleInputOnChange = (e) => {
    const search = e.target.value
    const { filter, onChange } = this.props
    var filteredData = []

    if (search) {
      filteredData = this.state.data.where((item) => {
        return filter
          ? filter(item)
          : item.toLowerCase().indexOf(search.toLowerCase()) >= 0
      })
    }

    this.setState({ filteredData: filteredData })

    onChange(e)
  }

  handleOnBlur = () => {
    this.setState({ filteredData: [] })
    if (this.props.onBlur) {
      this.props.onBlur()
    }
  }

  focus() {
    this.inputRef.focus()
  }

  render() {
    const { activedIndex, filteredData } = this.state
    return (
      <React.Fragment>
        <InputCons
          {...this.props}
          inputRef={(inputRef) => (this.inputRef = inputRef)}
          onChange={this.handleInputOnChange}
          onKeyDown={this.handleOnKeyDown}
          onBlur={this.handleOnBlur}
        />

        <Suggestions
          style={this.props.style}
          onClick={this.handleSelect}
          activedIndex={activedIndex}
        >
          {filteredData}
        </Suggestions>
      </React.Fragment>
    )
  }
}

export default InputAutoSuggest
